screen.userinput:
cat > /etc/xinetd.conf << "EOF" # Début de /etc/xinetd # Fichier de configuration pour xinetd #
defaults { instances = 60 log_type = SYSLOG daemon log_on_success = HOST PID USERID log_on_failure = HOST USERID cps = 25 30 }
# Tou! les fichiers de service sont stockés dans le répertoire /etc/xinetd.d # includedir /etc/xinetd.d # Fin de /etc/xinetd EOF

install -v -d -m755 /etc/xinetd.d && cat > /etc/xinetd.d/login << "EOF" && # Début de /etc/xinetd.d/login
service login { disable = yes socket_type = stream protocol = tcp wait = no user = root server = /usr/sbin/in.rlogind log_type = SYSLOG local4 info }
# Fin de /etc/xinetd.d/login EOF cat > /etc/xinetd.d/shell << "EOF" && # Début de /etc/xinetd.d/shell
service shell { disable = yes socket_type = stream wait = no user = root instances = UNLIMITED flags = IDONLY log_on_success += USERID server = /usr/sbin/in.rshd }
# Fin de /etc/xinetd.d/shell EOF cat > /etc/xinetd.d/exec << "EOF" && # Début de /etc/xinetd.d/exec
service exec { disable = yes socket_type = stream wait = no user = root server = /usr/sbin/in.rexecd }
# Fin de /etc/xinetd.d/exec EOF cat > /etc/xinetd.d/comsat << "EOF" && # Début de /etc/xinetd.d/comsat
service comsat { disable = yes socket_type = dgram wait = yes user = nobody group = tty server = /usr/sbin/in.comsat }
# Fin de /etc/xinetd.d/comsat EOF cat > /etc/xinetd.d/talk << "EOF" && # Début de /etc/xinetd.d/talk
service talk { disable = yes socket_type = dgram wait = yes user = root server = /usr/sbin/in.talkd }
# Fin de /etc/xinetd.d/talk EOF cat > /etc/xinetd.d/ntalk << "EOF" && # Début de /etc/xinetd.d/ntalk
service ntalk { disable = yes socket_type = dgram wait = yes user = root server = /usr/sbin/in.ntalkd }
# Fin de /etc/xinetd.d/ntalk EOF cat > /etc/xinetd.d/telnet << "EOF" && # Début de /etc/xinetd.d/telnet
service telnet { disable = yes socket_type = stream wait = no user = root server = /usr/sbin/in.telnetd bind = 127.0.0.1 log_on_failure += USERID }
service telnet { disable = yes socket_type = stream wait = no user = root # server = /usr/sbin/in.telnetd bind = 192.231.139.175 redirect = 128.138.202.20 23 log_on_failure += USERID }
# Fin de /etc/xinetd.d/telnet EOF cat > /etc/xinetd.d/ftp << "EOF" && # Début de /etc/xinetd.d/ftp
service ftp { disable = yes socket_type = stream wait = no user = root server = /usr/sbin/in.ftpd server_args = -l instances = 4 log_on_success += DURATION USERID log_on_failure += USERID access_times = 2:00-8:59 12:00-23:59 nice = 10 }
# Fin de /etc/xinetd.d/ftp EOF cat > /etc/xinetd.d/tftp << "EOF" && # Début de /etc/xinetd.d/tftp
service tftp { disable = yes socket_type = dgram wait = yes user = root server = /usr/sbin/in.tftpd server_args = -s /tftpboot }
# Fin de /etc/xinetd.d/tftp EOF cat > /etc/xinetd.d/finger << "EOF" && # Début de /etc/xinetd.d/finger
service finger { disable = yes socket_type = stream wait = no user = nobody server = /usr/sbin/in.fingerd }
# Fin de /etc/xinetd.d/finger EOF cat > /etc/xinetd.d/systat << "EOF" && # Début de /etc/xinetd.d/systat
service systat { disable = yes socket_type = stream wait = no user = nobody server = /usr/bin/ps server_args = -auwwx only_from = 128.138.209.0 log_on_success = HOST }
# Fin de /etc/xinetd.d/systat EOF cat > /etc/xinetd.d/netstat << "EOF" && # Début de /etc/xinetd.d/netstat
service netstat { disable = yes socket_type = stream wait = no user = nobody server = /usr/ucb/netstat server_args = -f inet only_from = 128.138.209.0 log_on_success = HOST }
# Fin de /etc/xinetd.d/netstat EOF cat > /etc/xinetd.d/echo << "EOF" && # Début de /etc/xinetd.d/echo
service echo { disable = yes type = INTERNAL id = echo-stream socket_type = stream protocol = tcp user = root wait = no }
service echo { disable = yes type = INTERNAL id = echo-dgram socket_type = dgram protocol = udp user = root wait = yes }
# Fin de /etc/xinetd.d/echo EOF cat > /etc/xinetd.d/chargen << "EOF" && # Début de /etc/xinetd.d/chargen
service chargen { disable = yes type = INTERNAL id = chargen-stream socket_type = stream protocol = tcp user = root wait = no }
service chargen { disable = yes type = INTERNAL id = chargen-dgram socket_type = dgram protocol = udp user = root wait = yes }
# Fin de /etc/xinetd.d/chargen EOF cat > /etc/xinetd.d/daytime << "EOF" && # Début de /etc/xinetd.d/daytime
service daytime { disable = yes type = INTERNAL id = daytime-stream socket_type = stream protocol = tcp user = root wait = no }
service daytime { disable = yes type = INTERNAL id = daytime-dgram socket_type = dgram protocol = udp user = root wait = yes }
# Fin de /etc/xinetd.d/daytime EOF cat > /etc/xinetd.d/time << "EOF" && # Début de /etc/xinetd.d/time
service time { disable = yes type = INTERNAL id = time-stream socket_type = stream protocol = tcp user = root wait = no }
service time { disable = yes type = INTERNAL id = time-dgram socket_type = dgram protocol = udp user = root wait = yes }
# Fin de /etc/xinetd.d/time EOF cat > /etc/xinetd.d/rstatd << "EOF" && # Début de /etc/xinetd.d/rstatd
service rstatd { disable = yes type = RPC flags = INTERCEPT rpc_version = 2-4 socket_type = dgram protocol = udp server = /usr/sbin/rpc.rstatd wait = yes user = root }
# Fin de /etc/xinetd.d/rstatd EOF cat > /etc/xinetd.d/rquotad << "EOF" && # Début de /etc/xinetd.d/rquotad
service rquotad { disable = yes type = RPC rpc_version = 1 socket_type = dgram protocol = udp wait = yes user = root server = /usr/sbin/rpc.rstatd }
# Fin de /etc/xinetd.d/rquotad EOF cat > /etc/xinetd.d/rusersd << "EOF" && # Début de /etc/xinetd.d/rusersd
service rusersd { disable = yes type = RPC rpc_version = 1-2 socket_type = dgram protocol = udp wait = yes user = root server = /usr/sbin/rpc.rusersd }
# Fin de /etc/xinetd.d/rusersd EOF cat > /etc/xinetd.d/sprayd << "EOF" && # Début de /etc/xinetd.d/sprayd
service sprayd { disable = yes type = RPC rpc_version = 1 socket_type = dgram protocol = udp wait = yes user = root server = /usr/sbin/rpc.sprayd }
# Fin de /etc/xinetd.d/sprayd EOF cat > /etc/xinetd.d/walld << "EOF" && # Début de /etc/xinetd.d/walld
service walld { disable = yes type = RPC rpc_version = 1 socket_type = dgram protocol = udp wait = yes user = nobody group = tty server = /usr/sbin/rpc.rwalld }
# Fin de /etc/xinetd.d/walld EOF cat > /etc/xinetd.d/irc << "EOF" # Début de /etc/xinetd.d/irc
service irc { disable = yes socket_type = stream wait = no user = root flags = SENSOR type = INTERNAL bind = 192.168.1.30 deny_time = 60 }
# Fin de /etc/xinetd.d/irc EOF
