screen.userinput:
cat > /etc/rc.d/init.d/adsl << "EOF" #!/bin/bash # Début $rc_base/init.d/adsl
# Basé sur le script sysklogd à partir de LFS-3.1 et précédent. # Réécrit par Gerard Beekmans - gerard@linuxfromscratch.org # script adsl écrit par DJ Lucas - dj@lucasit.com
source /etc/sysconfig/rc source $rc_functions
case "$1" in start) echo "Lancement de l'interface ADSL..." /usr/sbin/adsl-start evaluate_retval ;;
stop) echo "Arrêt de l'interface ADSL..." /usr/sbin/adsl-stop evaluate_retval ;;
restart) $0 stop sleep 1 $0 start ;;
status) /usr/sbin/adsl-status ;;
*) echo "Usage: $0 {start|stop|restart|status}" exit 1 ;; esac
# End $rc_base/init.d/adsl EOF chmod 755 /etc/rc.d/init.d/adsl
