screen.userinput:
cat > /etc/rc.d/init.d/apache << "EOF" #!/bin/bash
source /etc/sysconfig/rc source $rc_functions
case "$1" in start) echo "Starting Apache daemon..." /usr/sbin/apachectl -k start evaluate_retval ;;
stop) echo "Stopping Apache daemon..." /usr/sbin/apachectl -k stop evaluate_retval ;;
restart) echo "Restarting Apache daemon..." /usr/sbin/apachectl -k restart evaluate_retval ;;
status) statusproc /usr/sbin/httpd ;;
*) echo "Usage: $0 {start|stop|restart|status}" exit 1 ;; esac EOF chmod 755 /etc/rc.d/init.d/apache
