screen.userinput:
cat > /etc/rc.d/init.d/proftp << "EOF" #!/bin/sh # Begin $rc_base/init.d/sshd
# Based on sysklogd script from LFS-3.1 and earlier. # Rewritten by Gerard Beekmans - gerard@linuxfromscratch.org
source /etc/sysconfig/rc source $rc_functions
case "$1" in start) echo "Starting FTP Server..." loadproc /usr/sbin/proftpd ;;
stop) echo "Stopping FTP Server..." killproc /usr/sbin/proftpd ;;
reload) echo "Reloading FTP Server..." reloadproc /usr/sbin/proftpd ;;
restart) $0 stop sleep 1 $0 start ;;
status) statusproc /usr/sbin/proftpd ;;
*) echo "Usage: $0 {start|stop|reload|restart|status}" exit 1 ;; esac
# End $rc_base/init.d/sshd EOF chmod 755 /etc/rc.d/init.d/proftpd
