userinput.command:
cat > /etc/rc.d/init.d/fcron << "EOF" #!/bin/sh # Début $rc_base/init.d/fcron
# Basé sur le script sysklogd script de LFS-3.1 et précédents. # Réécrit par Gerard Beekmans - gerard@linuxfromscratch.org
source /etc/sysconfig/rc source $rc_functions
case "$1" in start) echo "Starting fcron..." loadproc fcron ;;
stop) echo "Stopping fcron..." killproc fcron ;;
restart) $0 stop sleep 1 $0 start ;;
status) statusproc fcron ;;
*) echo "Usage: $0 {start|stop|restart|status}" exit 1 ;; esac
# Fin $rc_base/init.d/fcron EOF chmod 755 /etc/rc.d/init.d/fcron
