para.screen:
cat > /etc/rc.d/init.d/alsa << "EOF" #!/bin/sh # Début $rc_base/init.d/alsa
# Basé sur le script sysklogd de LFS-3.1 et précédents. # Réécrit par Gerard Beekmans - gerard@linuxfromscratch.org # Parties spécifiques à ALSA par Mark Hymers - markh@linuxfromscratch.org # Stockage des paramètrages du mixage dans l'emplacement par défaut: /etc/asound.state
source /etc/sysconfig/rc source $rc_functions
case "$1" in start) echo -n "Starting alsa... Restoring volumes..." loadproc /usr/sbin/alsactl restore #echo -n " Loading MIDI font..." #loadproc sfxload /path/to/soundfont ;;
stop) echo -n "Stopping alsa... Saving volumes......" loadproc /usr/sbin/alsactl store #echo -n " Removing MIDI font.........." #loadproc sfxload -i ;;
restart) $0 stop /usr/bin/sleep 1 $0 start ;;
*) echo "Usage: $0 {start|stop|restart}" exit 1 ;;
esac
# Fin $rc_base/init.d/alsa EOF chmod 755 /etc/rc.d/init.d/alsa
