screen.userinput:
cat > /etc/rc.d/init.d/exim << "EOF" #!/bin/sh # Début $rc_base/init.d/exim
# Basé sur le script sysklogd de LFS-3.1 et précédents. # Réécrit par Gerard Beekmans - gerard@linuxfromscratch.org
source /etc/sysconfig/rc source $rc_functions
case "$1" in
start) echo "Starting exim..." loadproc /usr/sbin/exim -bd -q1m ;;
stop) echo "Stopping exim..." killproc exim ;;
status) statusproc exim ;;
restart) $0 stop sleep 1 $0 start ;;
*) echo "Usage: $0 {start|stop|status|restart}" exit 1 ;; esac
# Fin $rc_base/init.d/exim EOF chmod 755 /etc/rc.d/init.d/exim
