screen.userinput:
cat > /etc/rc.d/init.d/qmail << "EOF" #!/bin/sh # Début $rc_base/init.d/qmail
# Basé sur le script sysklogd de LFS-3.1 et précédents. # Réécrit par Gerard Beekmans - gerard@linuxfromscratch.org
source /etc/sysconfig/rc source $rc_functions
case "$1" in
start) echo "Starting Qmail..." /var/qmail/rc & ;;
stop) echo "Stopping Qmail..." killall qmail-send ;;
restart) $0 stop sleep 1 $0 start ;;
*) echo "Usage: $0 {start|stop|restart}" exit 1 ;; esac
# Fin $rc_base/init.d/qmail EOF chmod 755 /etc/rc.d/init.d/qmail
