para.screen:
cat > /etc/rc.d/init.d/dhcp << "EOF" #!/bin/sh # Begin $rc_base/init.d/dhcp
# Based on sysklogd script from LFS-3.1 and earlier. # Rewritten by Gerard Beekmans - gerard@linuxfromscratch.org
source /etc/sysconfig/rc source $rc_functions
case "$1" in start) echo "Starting DHCP Server..." loadproc dhcpd -q eth1 ;;
stop) echo "Stopping DHCP Server..." killproc dhcpd ;;
reload) echo "Reloading DHCP Server..." reloadproc dhcpd ;;
restart) $0 stop sleep 1 $0 start ;;
status) statusproc dhcpd ;;
*) echo "Usage: $0 {start|stop|reload|restart|status}" exit 1 ;; esac
# End $rc_base/init.d/dhcp EOF chmod 755 /etc/rc.d/init.d/dhcp

cat > /etc/dhcpd.conf << "EOF"
default-lease-time 72000;
max-lease-time 144000;

subnet 192.168.5.0 netmask 255.255.255.0 {
  range 192.168.5.10 192.168.5.240;
  option broadcast-address 195.168.5.255;
  option routers 192.168.5.1;
}
EOF
