screen.userinput:
cat > /etc/rc.d/init.d/sshd << "EOF" #!/bin/sh # Début $rc_base/init.d/sshd
# Basé sur le script sysklogd de LFS-3.1 et précédents. # Réécrit par Gerard Beekmans - gerard@linuxfromscratch.org
source /etc/sysconfig/rc source $rc_functions
case "$1" in start) echo "Starting SSH Server..." loadproc /usr/sbin/sshd ;;
stop) echo "Stopping SSH Server..." killproc /usr/sbin/sshd ;;
reload) echo "Reloading SSH Server..." reloadproc /usr/sbin/sshd ;;
restart) $0 stop sleep 1 $0 start ;;
status) statusproc /usr/sbin/sshd ;;
*) echo "Usage: $0 {start|stop|reload|restart|status}" exit 1 ;; esac
# Fin $rc_base/init.d/sshd EOF chmod 755 /etc/rc.d/init.d/sshd
