screen.userinput:
cat > multiarch_wrapper.c << "EOF"
#include <unistd.h>
#include <stdlib.h>
#include <errno.h>
#include <string.h>

#ifndef USE_ARCH
#define USE_ARCH "64"
#endif

int main(int argc, char **argv)
{
  char *filename;
  char *use_arch;

  if(!(use_arch = getenv("USE_ARCH")))
    use_arch = USE_ARCH;

  filename = (char *) malloc(strlen(argv[0]) + strlen(use_arch) + 2);
  strcpy(filename, argv[0]);
  strcat(filename, "-");
  strcat(filename, use_arch);

  execvp(filename, argv);
  perror(argv[0]);
  free(filename);
}
EOF
